mapper.update:
update blog_posts
set post_subj = #{post_subj}, post_content = #{post_content}, post_img=#{post_img}
where post_id = #{post_id}

mapper.select:
select * from blog_posts where post_id > 0
AND (post_subj like CONCAT('%', #{keyword}, '%') or post_content like CONCAT('%', #{keyword}, '%'))
order by post_id desc, post_reg desc limit #{startList}, #{listSize}

select * from blog_posts where category_id = #{category_id}
AND (post_subj like CONCAT('%', #{keyword}, '%') or post_content like CONCAT('%', #{keyword}, '%'))
order by post_id desc, post_reg desc limit #{startList}, #{listSize}
